Properties.Octopus.Action.Script.ScriptBody:
function allowIp([string]$Website, [string]$ip, [string]$mask) {
    $value = @{ipAddress="$ip";allowed="true"}
    if ($mask -ne "")
    {
        Write-Output "Adding ip $ip/$mask to allow"
        $value.subnetMask = $mask
    }
    else
    {
         Write-Output "Adding ip $ip to allow"
    }

    add-webconfiguration /system.webServer/security/ipSecurity -location $Website -value $value -pspath IIS://
}

Clear-WebConfiguration -Filter /system.webServer/security/ipSecurity -location $Site

if ($EnableProxyMode -eq "true") {
    Write-Output "Enabling proxy mode"
    #set-webconfigurationproperty -Filter /system.webServer/security/dynamicIpSecurity -location $Site -Name "enableProxyMode" -Value "true"
    set-webconfigurationproperty -Filter /system.webServer/security/ipSecurity -location $Site -Name "enableProxyMode" -Value "true"
}

if ($SetDeny -eq "true") {
    Write-Output "Setting Deny rule"
    set-webconfigurationproperty -Filter /system.webServer/security/ipSecurity -location $Site -Name "allowUnlisted" -Value "false"
}

$ips = $IpAddresses -split '\n'
$ips = $ips.Trim('\r')
foreach ($u in $ips) {
    $a = $u.Split("/")
    allowIp $Site $a[0] $a[1]
}
